import React, { Component } from 'react';
import "./Slidebar.css"
import arrow_right from "../images/arrow_right.png";
import arrow_left  from "../images/arrow_left.png";
//import Footer from "../component/Footer"
import LoginChange from "./LoginChange";
import VisualChange from "./VisualChange";
//import {NavBar_in} from '../component/AppBar_in';

class Slidebar extends Component{
    constructor(props){
        super(props);
        this.state = {
            clicktime:0
        }

        this.slideopen = this.slideopen.bind(this);
        this.slideclose = this.slideclose.bind(this);

    }

    slideopen(e){
        e.preventDefault();
        let slidebtn = document.getElementById("Slidebar_btn_close");
        let slideicon = document.getElementsByClassName("Slidebar_png");
        slidebtn.style.left = "44vw"
        slidebtn.style.transitionProperty = "left";
        slidebtn.style.transitionDuration = "0.5s"
        slidebtn.id = "Slidebar_btn_open";
        slidebtn.onclick = this.slideclose;
        slideicon[0].src = arrow_left;

    }
    slideclose(e){
        e.preventDefault();
        let slidebtn = document.getElementById("Slidebar_btn_open");
        let slideicon = document.getElementsByClassName("Slidebar_png");
        slidebtn.style.left = "1vw"
        slidebtn.style.transitionProperty = "left";
        slidebtn.style.transitionDuration = "0.5s"
        slidebtn.id = "Slide_btn_open";
        slidebtn.onclick = this.slideopen;
        slideicon[0].src = arrow_right;
    }
    render(){
        return(
            <div id="Slidebar_container">
                <button class="Slidebar_btn" id = "Slidebar_btn_close">
                    <img class="Slidebar_png" src={arrow_right} alt="arrow_right" onClick={this.slideopen}/>
                </button>
                    {/*<button id="Slidebar_btn_open">
                        <img class="Slidebar_png" src={arrow_left} alt="arrow_left"/>
                    </button>
                    <VisualChange />
                    <LoginChange />  */}
        </div>
        )



    }
}

export default Slidebar;
