import React, { Component } from "react";
import "./column_block_anno.css";

class Column_block_anno extends Component{
    constructor(props){
        super(props);
        this.state = {
            anno : this.props.anno,
            id : this.props.id
        }
        this.addAnno = this.addAnno.bind(this);
    }
    addAnno(i,content){
        let title_container = document.getElementById(this.state.id);
        let newtitle = document.createElement("p");
        newtitle.setAttribute('href','#');
        newtitle.setAttribute('class','column_title');
        newtitle.setAttribute('id',this.state.id+"_"+i);
        newtitle.innerHTML = content;
        title_container.appendChild(newtitle);
    }

    render(){
        return(
            <div id={this.state.id} class="column_block_anno">
                {this.state.anno}
                {this.state.date}
            </div>
        )
    }
}

export default Column_block_anno;
